(function(){tinymce.create('tinymce.plugins.ItemsPlugin',{init:function(ed,url){var self=this;ed.addCommand('mcePasteItem',function(item){if(!item.html){return}var html=item.html;var dom=ed.dom;ed.execCommand('mceInsertContent',false,html)});ed.addCommand('mceItems',function(){ed.windowManager.open({file:url+'/items.htm',width:320+ed.getLang('example.delta_width',0),height:120+ed.getLang('example.delta_height',0),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('items',{title:'Insert Item',cmd:'mceItems',image:url+'/locker.png'})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Example plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"}}});tinymce.PluginManager.add('items',tinymce.plugins.ItemsPlugin)})();
