(function(){tinymce.create('tinymce.plugins.ItemsPlugin',{init:function(ed,url){var self=this;ed.addCommand('mcePasteItem',function(){console.log(arguments);var dom=ed.dom;var node=dom.create('div',0,'some content');ed.execCommand('mceInsertContent',false,'<b>Hello world!!</b>')});ed.addCommand('mceItems',function(){ed.windowManager.open({file:url+'/items.htm',popup_css:url+'/css/items.css',width:320+ed.getLang('example.delta_width',0),height:120+ed.getLang('example.delta_height',0),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('items',{title:'items.desc',cmd:'mceItems'})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'Example plugin',author:'Some author',authorurl:'http://tinymce.moxiecode.com',infourl:'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/example',version:"1.0"}}});tinymce.PluginManager.add('items',tinymce.plugins.ItemsPlugin)})();
